import keras
from keras.models import Sequential
from keras.layers import Dense, LSTM

from bitstring import BitArray as bt

from src.utils import *
import numpy as np
import pandas as pd
import mido
import os


np.random.seed(100)

np.set_printoptions(linewidth=400)

lowerBound = 24
upperBound = 102

BATCH_SIZE = 1000

INPUT_SIZE = 100
MESSAGE_SIZE = upperBound - lowerBound

EPOCH = 100

class Aarohi():
	"""docstring for Aarohi"""
	def __init__(self):

		self.model = Sequential()

		# self.model.add(Dense(MESSAGE_SIZE * 4, input_shape=(INPUT_SIZE, MESSAGE_SIZE * 2), activation='relu'))
		self.model.add(LSTM(MESSAGE_SIZE * 4, input_shape=(INPUT_SIZE, MESSAGE_SIZE * 2), return_sequences=True))
		self.model.add(LSTM(MESSAGE_SIZE * 2, return_sequences=True))
		self.model.add(LSTM(MESSAGE_SIZE * 2))
		self.model.add(Dense(MESSAGE_SIZE * 2, activation='sigmoid'))

		self.model.compile(loss='binary_crossentropy', optimizer='adagrad', metrics=['accuracy'])
		self.model.summary()

	def load_model(self, filename):

		self.model = keras.models.load_model(filename)


	def setTrainingData(self, filespath):

		train_data = []

		for path, dirs, files in os.walk(filespath):
			for file in files[:2]:
				if file[-4:] != ".mid":
					continue
				
				print(file)
				noteMatrix = midiToNoteMatrix(filespath + file)
				test = np.array(noteMatrix).shape
				if(len(noteMatrix) < 2 or test[1] != 156):
					continue

				print(np.array(noteMatrix).shape)

				train_data.extend(noteMatrix)

		# x_train = np.zeros((len(train_data)-INPUT_SIZE-1, MESSAGE_SIZE * 2))
		# y_train = np.zeros((MESSAGE_SIZE * 2))
		
		self.x_train = np.expand_dims(train_data[0:INPUT_SIZE], axis=0)
		self.y_train = np.expand_dims(train_data[INPUT_SIZE + 1], axis=0)

		# print(len(train_data)-INPUT_SIZE-1)
		for i in range(1,len(train_data)-INPUT_SIZE-1):
			self.x_train = np.append(self.x_train, np.expand_dims(train_data[i:i + INPUT_SIZE], axis=0) )
			self.y_train = np.append(self.y_train, np.expand_dims(train_data[i + INPUT_SIZE + 1], axis=0) )
		
		print(self.x_train.shape)
		print(self.y_train.shape)

	def train(self):

		self.model.fit(self.x_train, self.y_train, epochs=EPOCH, batch_size=BATCH_SIZE)

	def save_model(self, filename):

		self.model.save(filename)

