np.random.seed(100)

np.set_printoptions(linewidth=400)

lowerBound = 24
upperBound = 102

BATCH_SIZE = 500

INPUT_SIZE = 20
MESSAGE_SIZE = upperBound - lowerBound

EPOCH = 100

