	def load_model(self, filename):
		self.model = keras.models.load_model(filename)


	def setTrainingData(self, filespath):

		train_data = []

		for path, dirs, files in os.walk(filespath):
			for file in files[:10]:
				if file[-4:] != ".mid":
					continue
				
				print(file)
				noteMatrix = midiToNoteMatrix(filespath + file)
				test = np.array(noteMatrix).shape
				if(len(noteMatrix) < 2 or test[1] != 156):
					continue

				print(np.array(noteMatrix).shape)

				train_data.extend(noteMatrix)

		self.x_train = []
		self.y_train = []

		for i in range(1,len(train_data)-INPUT_SIZE-1):
			if (i + 1) % 100 == 0:
				print(i + 1, len(train_data)-INPUT_SIZE-1)

			self.x_train.append(train_data[i:i+INPUT_SIZE])
			self.y_train.append(train_data[i + INPUT_SIZE + 1])

		self.x_train = np.asarray(self.x_train)
		self.y_train = np.asarray(self.y_train)
		print(self.x_train.shape)
		print(self.y_train.shape)

	def train(self):

		self.model.fit(self.x_train, self.y_train, epochs=EPOCH, batch_size=BATCH_SIZE)

	def save_model(self, filename):

		self.model.save(filename)

